class Calculadora extends React.Component {
    state = {
        res: "0"
    }
    onDigit = (digit)=>{
        console.log(digit)
    }
    //altertar = () => {
      //  alert("Hola soy un metodo de clase") }
    
    render() {
    // const alerta2 = ()=>alert("Hola desde alerta2")//
        return (
            <div>
                <div className="z-depth-2 light-blue lighten-2 white-text center-align resultados">
                    {this.state.res}
                </div>
                <div style={{ textAlign: "center" }}>
                    <a className="waves-effect waves-light btn" onClick={()=>this.onDigit("0")}>0</a>
                    <a className="waves-effect waves-light btn" onClick={()=>this.setState({res:"1"})}>1</a>
                    <a className="waves-effect waves-light btn" onClick={()=>this.setState({res:"2"})} >2</a>
                </div>
                <style jsx>
                {`
                .resultados{
                    width:300px;
                    margin:0 auto;
                    font-size:32px;
                    margin-bottom:10px;
                }`
                }
                </style>
            </div>
        )
    }
}
export default Calculadora;
